        from collections import deque

        def Wypelnij_niedostepne_Nonami_lewy_gorny(Labirynt):
            n = len(Labirynt)
            m = len(Labirynt[0]) if n > 0 else 0

            # BFS
            queue = deque([(0, 0)])
            while queue:
                x, y = queue.popleft()
                if Labirynt[x][y] == 0:
                    Labirynt[x][y] = 'V'  # 'V' oznacza, że komórka była odwiedzona

                    # Sprawdzanie sąsiadów
                    for dx, dy in [(0, 1), (1, 0), (-1, 0)]:
                        nx, ny = x + dx, y + dy
                        if 0 <= nx < n and 0 <= ny < m and Labirynt[nx][ny] == 0:
                            queue.append((nx, ny))

            # Oznaczanie niedostępnych komórek jako None
            for i in range(n):
                for j in range(m):
                    if Labirynt[i][j] == 0:
                        Labirynt[i][j] = None
                    elif Labirynt[i][j] == 'V':
                        Labirynt[i][j] = 0

            return Labirynt

        def Wypelnij_niedostepne_Nonami_prawny_dolny(Labirynt):
            n = len(Labirynt)
            m = len(Labirynt[0]) if n > 0 else 0

            # BFS starting from T[n-1][n-1]
            queue = deque([(n - 1, n - 1)])
            while queue:
                x, y = queue.popleft()
                if Labirynt[x][y] == 0:
                    Labirynt[x][y] = 'V'  # 'V' oznacza, że komórka była odwiedzona

                    # Sprawdzanie sąsiadów
                    for dx, dy in [(0, -1), (-1, 0), (1, 0)]:
                        nx, ny = x + dx, y + dy
                        if 0 <= nx < n and 0 <= ny < m and Labirynt[nx][ny] == 0:
                            queue.append((nx, ny))

            # Oznaczanie niedostępnych komórek jako None
            for i in range(n):
                for j in range(m):
                    if Labirynt[i][j] == 0:
                        Labirynt[i][j] = None
                    elif Labirynt[i][j] == 'V':
                        Labirynt[i][j] = 0

            return Labirynt

        Labi = Wypelnij_niedostepne_Nonami_lewy_gorny(Labi)
        Labi = Wypelnij_niedostepne_Nonami_prawny_dolny(Labi)

